import * as Fetch from "bs-fetch/src/Fetch.js";
import * as Js_dict from "bs-platform/lib/es6/js_dict.js";
import * as Caml_option from "bs-platform/lib/es6/caml_option.js";
import * as ReasonRelay from "reason-relay/src/ReasonRelay.bs.js";
import * as Caml_exceptions from "bs-platform/lib/es6/caml_exceptions.js";
import * as Caml_chrome_debugger from "bs-platform/lib/es6/caml_chrome_debugger.js";

var Graphql_error = Caml_exceptions.create("RelayEnv-NextjsDefault.Graphql_error");

function fetchQuery(operation, variables, _cacheConfig) {
  return fetch("http://localhost:4000/api", Fetch.RequestInit.make(/* Post */2, {
                      "content-type": "application/json",
                      accept: "application/json"
                    }, Caml_option.some(JSON.stringify(Js_dict.fromList(/* :: */Caml_chrome_debugger.simpleVariant("::", [
                                    /* tuple */[
                                      "query",
                                      operation.text
                                    ],
                                    /* :: */Caml_chrome_debugger.simpleVariant("::", [
                                        /* tuple */[
                                          "variables",
                                          variables
                                        ],
                                        /* [] */0
                                      ])
                                  ])))), undefined, undefined, undefined, undefined, undefined, undefined, undefined, undefined)(undefined)).then((function (resp) {
                if (resp.ok) {
                  return resp.json();
                } else {
                  return Promise.reject([
                              Graphql_error,
                              "Request failed: " + resp.statusText
                            ]);
                }
              }));
}

var network = ReasonRelay.Network.makePromiseBased(fetchQuery, undefined, undefined);

var environment = ReasonRelay.Environment.make(network, ReasonRelay.Store.make(ReasonRelay.RecordSource.make(undefined, undefined), undefined, undefined), undefined, undefined, undefined);

export {
  Graphql_error ,
  fetchQuery ,
  network ,
  environment ,

}
/* network Not a pure module */
